# En utils/audio_manager.pyimport pygameclass AudioManager:    def __init__(self):        pygame.mixer.init()        self.musica_actual = None            def reproducir_musica(self, archivo, loop=True):        """Reproduce música de fondo"""        pygame.mixer.music.load(archivo)        pygame.mixer.music.play(-1 if loop else 0)        def detener_musica(self):        """Detiene la música"""        pygame.mixer.music.stop()        def cambiar_volumen(self, volumen):        """Cambia volumen (0.0 a 1.0)"""        pygame.mixer.music.set_volume(volumen)